mapper.select:
select count(*) FROM SPACE S, SPACE_CATEGORY SC, SPACE_ATTACHMENT SA , (SELECT S.SPACE_NO, MIN(PRICE) PRICE, RO.START_DATE, RO.END_DATE FROM SPACE S, ROOM RO WHERE S.SPACE_NO(+) = RO.SPACE_NO
AND #{checkIn} BETWEEN RO.START_DATE AND RO.END_DATE AND #{checkOut} BETWEEN RO.START_DATE AND RO.END_DATE
AND RO.PEOPLE_COUNT >= #{people}
GROUP BY S.SPACE_NO, RO.START_DATE, RO.END_DATE ORDER BY 1 ) EEE WHERE S.SPACE_NO = SA.SPACE_NO AND S.SP_TYPE = SC.SP_TYPE AND SA.STEP = 1 and s.space_no = eee.space_no and ( S.SPACE_TITLE LIKE '%'||#{keyword}||'%' or S.SPACE_SUBTITLE LIKE '%'||#{keyword}||'%' or S.SPACE_DESCRIPTION like '%'||#{keyword}||'%' or SC.CATEGORY_DETAIL LIKE '%'||#{keyword}||'%' or S.ADDRESS LIKE '%'||#{keyword}||'%' or S.HASHTAG LIKE '%'||#{keyword}||'%' )
AND EEE.PRICE BETWEEN #{minPrice} AND #{maxPrice}
AND SC.CATEGORY_DETAIL=#{category}
AND S.ADDRESS LIKE '%제주시%'
AND S.ADDRESS LIKE '%서귀포시%'

mapper.update:
UPDATE SPACE_CATEGORY 
SET CATEGORY_NAME=#{categoryName}
,CATEGORY_DETAIL=#{categoryDetail}
WHERE SP_TYPE=#{spType}

UPDATE SPACE_FACILITY
SET FACILITY_NAME=#{facilityName}, CATEGORY_DETAIL=#{categoryDetail}
WHERE SF_NO=#{sfNo}

mapper.select:
SELECT COUNT(*) FROM(SELECT SPACE_NO, sc.CATEGORY_NAME,sc.CATEGORY_DETAIL, SPACE_TITLE,SP_TYPE, SPACE_SUBTITLE, SPACE_DESCRIPTION,ADDRESS,HASHTAG, STEP, sa.CHANGE_NAME, MIN(PRICE) "PRICE" FROM SPACE s JOIN SPACE_CATEGORY sc USING(SP_TYPE) JOIN SPACE_ATTACHMENT sa USING(SPACE_NO) JOIN ROOM r USING(SPACE_NO) WHERE s.STATUS='Y' AND STEP=1 AND CATEGORY_NAME=#{categoryName}
AND PRICE BETWEEN #{minPrice} AND #{maxPrice}
AND #{checkIn} BETWEEN START_DATE AND END_DATE AND #{checkOut} BETWEEN START_DATE AND END_DATE
AND sc.CATEGORY_DETAIL=#{category}
AND PEOPLE_COUNT>= #{people}
AND ADDRESS LIKE '%제주시%'
AND ADDRESS LIKE '%서귀포시%'
GROUP BY SPACE_NO, sc.CATEGORY_NAME,sc.CATEGORY_DETAIL,SPACE_TITLE,SP_TYPE, SPACE_SUBTITLE, SPACE_DESCRIPTION,ADDRESS,HASHTAG, STEP, sa.CHANGE_NAME)

SELECT *
FROM SPACE_CATEGORY
WHERE CATEGORY_NAME=#{categoryName}

SELECT COUNT(*)
FROM LIKE_LIST
where member_no = #{memberNo}

SELECT COUNT(*)
FROM LIKE_LIST
where member_no = #{memberNo}
and space_no = #{spaceNo}

mapper.delete:
delete from like_list 
where member_no = #{memberNo}
and space_no = #{spaceNo}

mapper.update:
UPDATE SPACE
SET SPACE_TITLE=#{spaceTitle}, SPACE_SUBTITLE=#{spaceSubTitle}, SPACE_DESCRIPTION=#{spaceDescription}, ADDRESS=#{address}, PHONE=#{phone}, HASHTAG=#{hashtag}
WHERE SPACE_NO=#{spaceNo}

UPDATE SPACE_ATTACHMENT
SET ORIGIN_NAME=#{originName}, CHANGE_NAME=#{changeName}
WHERE SA_NO=#{saNo}
